import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import './Login.css';

const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:5000';

function Login({ onLogin }) {
    const [loginData, setLoginData] = useState({
        username: '',
        email: '',
        password: ''
    });
    const [loginMethod, setLoginMethod] = useState('username'); // 'username' or 'email'
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        try {
            console.log('尝试登录:', {
                url: `${API_BASE}/api/auth/login`,
                data: {
                    ...(loginMethod === 'username' ? { username: loginData.username } : { email: loginData.email }),
                    password: loginData.password
                }
            });

            const loginAxios = axios.create({
                baseURL: API_BASE,
                timeout: 5000,
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json'
                }
            });

            const response = await loginAxios.post('/api/auth/login', {
                ...(loginMethod === 'username' ? { username: loginData.username } : { email: loginData.email }),
                password: loginData.password
            });

            console.log('登录响应:', response);

            if (response.data.token) {
                localStorage.setItem('token', response.data.token);
                onLogin(response.data.user);
                navigate('/');
            } else {
                throw new Error('登录响应中没有token');
            }
        } catch (error) {
            console.error('登录失败:', error);
            let errorMessage = '登录失败，请重试';
            
            if (error.response) {
                console.error('错误响应:', error.response);
                errorMessage = error.response.data?.error || errorMessage;
            } else if (error.code === 'ECONNABORTED') {
                console.error('请求超时');
                errorMessage = '服务器响应超时，请检查后端服务是否正常运行';
            } else if (error.request) {
                console.error('未收到响应:', error.request);
                errorMessage = '无法连接到服务器，请检查网络连接和后端服务';
            } else {
                console.error('请求错误:', error.message);
                errorMessage = error.message;
            }
            
            setError(errorMessage);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="login-container">
            <div className="login-box">
                <h2>登录</h2>
                <div className="login-method-selector">
                    <button
                        type="button"
                        className={loginMethod === 'username' ? 'active' : ''}
                        onClick={() => setLoginMethod('username')}
                    >
                        使用用户名
                    </button>
                    <button
                        type="button"
                        className={loginMethod === 'email' ? 'active' : ''}
                        onClick={() => setLoginMethod('email')}
                    >
                        使用邮箱
                    </button>
                </div>
                <form onSubmit={handleSubmit}>
                    {loginMethod === 'username' ? (
                        <div className="form-group">
                            <label>用户名</label>
                            <input
                                type="text"
                                value={loginData.username}
                                onChange={(e) => setLoginData({...loginData, username: e.target.value})}
                                placeholder="请输入用户名"
                                required
                                disabled={loading}
                            />
                        </div>
                    ) : (
                        <div className="form-group">
                            <label>邮箱</label>
                            <input
                                type="email"
                                value={loginData.email}
                                onChange={(e) => setLoginData({...loginData, email: e.target.value})}
                                placeholder="请输入邮箱"
                                required
                                disabled={loading}
                            />
                        </div>
                    )}
                    <div className="form-group">
                        <label>密码</label>
                        <input
                            type="password"
                            value={loginData.password}
                            onChange={(e) => setLoginData({...loginData, password: e.target.value})}
                            placeholder="请输入密码"
                            required
                            disabled={loading}
                        />
                    </div>
                    {error && <div className="error-message">{error}</div>}
                    <button 
                        type="submit" 
                        disabled={loading}
                        className={loading ? 'loading' : ''}
                    >
                        {loading ? '登录中...' : '登录'}
                    </button>
                </form>
                <p className="auth-link">
                    还没有账号？ <Link to="/register">立即注册</Link>
                </p>
            </div>
        </div>
    );
}

export default Login;
