import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Input, Card, Button, Space, message, Modal, Form } from 'antd';
import { SearchOutlined, FileTextOutlined, PlusOutlined } from '@ant-design/icons';
import axios from 'axios';
import './PatientList.css';

const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:5000';

function PatientList() {
    const [patients, setPatients] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [searchTerm, setSearchTerm] = useState('');
    const [showReportModal, setShowReportModal] = useState(false);
    const [selectedPatient, setSelectedPatient] = useState(null);
    const [form] = Form.useForm();

    useEffect(() => {
        fetchPatients();
    }, []);

    const fetchPatients = async () => {
        try {
            setLoading(true);
            const response = await axios.get(`${API_BASE}/api/patients`, {
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                }
            });
            setPatients(response.data.patients);
        } catch (error) {
            setError('获取患者列表失败');
            console.error('获取患者列表错误:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleSearch = (value) => {
        setSearchTerm(value);
    };

    const handleGenerateReport = async (patientId) => {
        try {
            setLoading(true);
            const response = await axios.get(
                `${API_BASE}/api/reports/${patientId}`,
                {
                    headers: {
                        'Authorization': `Bearer ${localStorage.getItem('token')}`
                    },
                    responseType: 'blob'
                }
            );

            const url = window.URL.createObjectURL(new Blob([response.data]));
            const link = document.createElement('a');
            link.href = url;
            link.setAttribute('download', `patient-report-${patientId}.pdf`);
            document.body.appendChild(link);
            link.click();
            link.remove();
            window.URL.revokeObjectURL(url);

            message.success('报告生成成功');
        } catch (error) {
            message.error('报告生成失败');
            console.error('生成报告错误:', error);
        } finally {
            setLoading(false);
        }
    };

    const filteredPatients = patients.filter(patient => {
        const searchLower = searchTerm.toLowerCase();
        return (
            patient.name.toLowerCase().includes(searchLower) ||
            patient.patient_id.toLowerCase().includes(searchLower)
        );
    });

    return (
        <div className="patient-list-container">
            <div className="patient-list-header">
                <h2>患者管理</h2>
                <Space>
                    <Input
                        placeholder="搜索患者..."
                        prefix={<SearchOutlined />}
                        onChange={(e) => handleSearch(e.target.value)}
                        style={{ width: 300 }}
                    />
                    <Button 
                        type="primary" 
                        icon={<PlusOutlined />}
                        onClick={() => setShowReportModal(true)}
                    >
                        新建患者
                    </Button>
                </Space>
            </div>

            {loading && <div className="loading">加载中...</div>}
            {error && <div className="error-message">{error}</div>}

            <div className="patient-grid">
                {filteredPatients.map(patient => (
                    <Card key={patient.id} className="patient-card">
                        <div className="patient-info">
                            <h3>{patient.name}</h3>
                            <p><strong>患者ID：</strong>{patient.patient_id}</p>
                            <p><strong>年龄：</strong>{patient.age}</p>
                            <p><strong>性别：</strong>{patient.gender}</p>
                            <p><strong>检查次数：</strong>{patient.image_count}</p>
                            <p><strong>首次检查：</strong>{new Date(patient.created_at).toLocaleDateString()}</p>
                        </div>
                        <div className="patient-actions">
                            <Space>
                                <Link to={`/patients/${patient.id}`}>
                                    <Button type="primary">查看详情</Button>
                                </Link>
                                <Button 
                                    icon={<FileTextOutlined />}
                                    onClick={() => handleGenerateReport(patient.id)}
                                    loading={loading}
                                >
                                    生成报告
                                </Button>
                            </Space>
                        </div>
                    </Card>
                ))}
            </div>

            <Modal
                title="新建患者"
                open={showReportModal}
                onCancel={() => setShowReportModal(false)}
                footer={null}
            >
                <Form
                    form={form}
                    onFinish={async (values) => {
                        try {
                            const response = await axios.post(
                                `${API_BASE}/api/patients`,
                                values,
                                {
                                    headers: {
                                        'Authorization': `Bearer ${localStorage.getItem('token')}`
                                    }
                                }
                            );
                            message.success('患者创建成功');
                            setShowReportModal(false);
                            form.resetFields();
                            fetchPatients();
                        } catch (error) {
                            message.error('患者创建失败');
                            console.error('创建患者错误:', error);
                        }
                    }}
                >
                    <Form.Item
                        name="name"
                        label="姓名"
                        rules={[{ required: true, message: '请输入患者姓名' }]}
                    >
                        <Input />
                    </Form.Item>
                    <Form.Item
                        name="patient_id"
                        label="患者ID"
                        rules={[{ required: true, message: '请输入患者ID' }]}
                    >
                        <Input />
                    </Form.Item>
                    <Form.Item
                        name="age"
                        label="年龄"
                        rules={[{ required: true, message: '请输入患者年龄' }]}
                    >
                        <Input type="number" min={0} max={150} />
                    </Form.Item>
                    <Form.Item
                        name="gender"
                        label="性别"
                        rules={[{ required: true, message: '请选择患者性别' }]}
                    >
                        <Select>
                            <Select.Option value="M">男</Select.Option>
                            <Select.Option value="F">女</Select.Option>
                        </Select>
                    </Form.Item>
                    <Form.Item>
                        <Button type="primary" htmlType="submit">
                            创建
                        </Button>
                        <Button onClick={() => setShowReportModal(false)} style={{ marginLeft: 8 }}>
                            取消
                        </Button>
                    </Form.Item>
                </Form>
            </Modal>
        </div>
    );
}

export default PatientList;
