// 导入 React 库中的 useState 和 useReducer 钩子，用于在函数组件中添加状态管理
import React, { useState, useReducer, useRef, useEffect, Suspense, lazy } from 'react';
// 导入 axios 库，用于发送 HTTP 请求
import axios from 'axios';
// 导入 react-router-dom 中的 BrowserRouter 重命名为 Router，Routes 和 Route 组件
import { BrowserRouter as Router, Routes, Route, Link, Navigate, useNavigate } from 'react-router-dom';
import './styles/dicom.css';
import './styles/App.css';

// 懒加载组件
const DICOMViewer = lazy(() => import('./components/Viewer/DICOMViewer'));
const ImageViewer = lazy(() => import('./components/Viewer/ImageViewer'));
const ComparisonView = lazy(() => import('./components/Viewer/ComparisonView'));
const DataManager = lazy(() => import('./components/DataManagement/DataManager'));
const Login = lazy(() => import('./components/Auth/Login'));
const Register = lazy(() => import('./components/Auth/Register'));
const PatientList = lazy(() => import('./components/Patient/PatientList'));
const PatientDetail = lazy(() => import('./components/Patient/PatientDetail'));
const ImageUpload = lazy(() => import('./components/ImageUpload/ImageUpload'));

// 新增环境变量配置
const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:5000';

// 创建axios实例
const axiosInstance = axios.create({
    baseURL: API_BASE,
    timeout: 10000,
    headers: {
        'Content-Type': 'application/json',
        'Accept': 'application/json'
    }
});

// 添加请求拦截器
axiosInstance.interceptors.request.use(
    (config) => {
        const token = localStorage.getItem('token');
        if (token) {
            config.headers['Authorization'] = `Bearer ${token}`;
        }
        console.log('发送请求:', {
            url: config.url,
            method: config.method,
            headers: config.headers,
            data: config.data
        });
        return config;
    },
    (error) => {
        console.error('请求错误:', error);
        return Promise.reject(error);
    }
);

// 添加响应拦截器
axiosInstance.interceptors.response.use(
    (response) => {
        console.log('收到响应:', response);
        return response;
    },
    (error) => {
        console.error('响应错误:', error);
        if (error.response?.status === 401) {
            localStorage.removeItem('token');
            window.location.href = '/login';
        }
        return Promise.reject(error);
    }
);

// 配置全局axios默认值
axios.defaults.baseURL = API_BASE;
axios.defaults.headers.common['Content-Type'] = 'application/json';
axios.defaults.headers.common['Accept'] = 'application/json';

// 认证状态管理
const authReducer = (state, action) => {
    switch (action.type) {
        case 'LOGIN':
            return { ...state, isAuthenticated: true, user: action.payload };
        case 'LOGOUT':
            return { ...state, isAuthenticated: false, user: null };
        default:
            return state;
    }
};

// 使用useReducer整合状态
const initialState = {
    taskId: null,
    status: 'idle', // 'idle' | 'uploading' | 'processing' | 'completed' | 'failed'
    progress: 0,
    result: null,
    error: null
};

function uploadReducer(state, action) {
    switch (action.type) {
        case 'START_UPLOAD':
            return { ...state, status: 'uploading', progress: 0 };
        case 'UPDATE_PROGRESS':
            return { ...state, progress: action.payload };
        case 'PROCESS_START':
            return { ...state, status: 'processing', taskId: action.payload };
        case 'SUCCESS':
            return { ...state, status: 'completed', result: action.payload };
        case 'ERROR':
            return { ...state, status: 'failed', error: action.payload };
        case 'RESET':
            return initialState;
        default:
            return state;
    }
}

const MainApp = () => {
    const [authState, authDispatch] = useReducer(authReducer, {
        isAuthenticated: !!localStorage.getItem('token'),
        user: null
    });
    const [state, dispatch] = useReducer(uploadReducer, initialState);
    const [selectedFile, setSelectedFile] = useState(null);
    const [uploadedImage, setUploadedImage] = useState(null);
    const [loading, setLoading] = useState(false);
    const [processingStatus, setProcessingStatus] = useState(null);
    const [processedImages, setProcessedImages] = useState({});
    const [analysisResults, setAnalysisResults] = useState(null);
    const [patients, setPatients] = useState([]);
    const [selectedPatient, setSelectedPatient] = useState(null);
    const [showPatientModal, setShowPatientModal] = useState(false);
    const [newPatient, setNewPatient] = useState({
        name: '',
        patient_id: '',
        age: '',
        gender: 'M'
    });
    const cancelTokenSource = useRef(null);
    const navigate = useNavigate();
    const pollInterval = useRef(null);
    const [previewImage, setPreviewImage] = useState(null);

    // 获取患者列表
    useEffect(() => {
        const fetchPatients = async () => {
            try {
                const response = await axiosInstance.get('/api/patients');
                console.log('获取到的患者列表:', response.data);
                if (response.data && Array.isArray(response.data.patients)) {
                    setPatients(response.data.patients);
                }
            } catch (error) {
                console.error('获取患者列表失败:', error);
                if (error.response?.status === 401) {
                    navigate('/login');
                }
            }
        };

        if (authState.isAuthenticated) {
            fetchPatients();
        }
    }, [authState.isAuthenticated, navigate]);

    // 配置axios默认值
    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            // 配置axios默认值
            axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
            axios.defaults.headers.common['Content-Type'] = 'application/json';
            axios.defaults.headers.common['Accept'] = 'application/json';
            axios.defaults.withCredentials = true;  // 允许跨域请求携带凭证
        }
    }, []);

    // 创建新患者
    const handleCreatePatient = async () => {
        try {
            const token = localStorage.getItem('token');
            if (!token) {
                console.error('未找到token');
                alert('请先登录');
                navigate('/login');
                return;
            }

            // 验证输入
            if (!newPatient.name || !newPatient.patient_id || !newPatient.age || !newPatient.gender) {
                alert('请填写所有必填字段');
                return;
            }

            const age = parseInt(newPatient.age, 10);
            if (isNaN(age) || age <= 0) {
                alert('请输入有效的年龄');
                return;
            }

            const patientData = {
                ...newPatient,
                age: age
            };

            console.log('正在发送创建患者请求:', patientData);

            const response = await axiosInstance.post('/api/patients', patientData);

            console.log('服务器响应:', response);

            if (response.data && response.data.patient) {
                alert('患者创建成功');
                setShowPatientModal(false);
                setNewPatient({
                    name: '',
                    patient_id: '',
                    age: '',
                    gender: 'M'
                });

                // 将新创建的患者添加到列表中
                setPatients(prevPatients => [...prevPatients, response.data.patient]);
            } else {
                throw new Error('服务器响应格式不正确');
            }
        } catch (error) {
            console.error('创建患者失败:', error);
            console.error('错误详情:', {
                message: error.message,
                response: error.response,
                request: error.request
            });
            
            let errorMessage = '创建患者失败，请重试';
            if (error.response) {
                errorMessage = error.response.data?.error || error.response.data?.message || errorMessage;
            } else if (error.request) {
                errorMessage = '无法连接到服务器，请检查网络连接';
            }
            
            alert(errorMessage);
        }
    };

    // 清理函数
    const cleanup = () => {
        if (pollInterval.current) {
            clearTimeout(pollInterval.current);
        }
        if (cancelTokenSource.current) {
            cancelTokenSource.current.cancel('操作已取消');
        }
    };

    // 组件卸载时清理
    useEffect(() => {
        return cleanup;
    }, []);

    // 定义一个处理文件选择事件的函数
    const handleFileChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            setSelectedFile(file);
            // 清除之前的状态
            setUploadedImage(null);
            setProcessingStatus(null);
            setProcessedImages({});
            setAnalysisResults(null);
            setPreviewImage(null);
            dispatch({ type: 'RESET' });
        }
    };

    // 处理图像上传
    const handleImageUpload = async (file, patientId) => {
        try {
            setLoading(true);
            setProcessingStatus('uploading');
            
            const formData = new FormData();
            formData.append('file', file);
            formData.append('patient_id', patientId);
            
            const response = await axios.post(`${API_BASE}/api/upload`, formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                }
            });
            
            if (response.data.status === 'success') {
                setProcessingStatus('uploaded');
                await startProcessing(response.data.file_info.id);
            }
        } catch (error) {
            console.error('上传失败:', error);
            setProcessingStatus('error');
        } finally {
            setLoading(false);
        }
    };

    // 开始处理图像
    const startProcessing = async (imageId) => {
        try {
            setProcessingStatus('processing');
            const response = await axios.post(`${API_BASE}/api/process/${imageId}`, {}, {
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                }
            });
            
            if (response.data.task_id) {
                await pollTaskStatus(response.data.task_id);
            }
        } catch (error) {
            console.error('处理失败:', error);
            setProcessingStatus('error');
        }
    };

    // 轮询任务状态
    const pollTaskStatus = async (taskId) => {
        try {
            const interval = setInterval(async () => {
                const response = await axios.get(`${API_BASE}/api/tasks/${taskId}`);
                const { status, progress, results, matlab_log } = response.data;
                
                if (status === 'completed') {
                    clearInterval(interval);
                    setProcessingStatus('completed');
                    setAnalysisResults(results);
                    await loadProcessedImages(taskId);
                } else if (status === 'failed') {
                    clearInterval(interval);
                    setProcessingStatus('failed');
                }
            }, 2000);
        } catch (error) {
            console.error('获取任务状态失败:', error);
            setProcessingStatus('error');
        }
    };

    // 加载处理后的图像
    const loadProcessedImages = async (taskId) => {
        try {
            const images = ['p1input.nii', 'p2input.nii', 'p3input.nii'];
            const loadedImages = {};
            
            for (const img of images) {
                const response = await axios.get(`${API_BASE}/api/processed/${taskId}/${img}`);
                if (response.data.status === 'success') {
                    loadedImages[img] = response.data.image;
                }
            }
            
            setProcessedImages(loadedImages);
        } catch (error) {
            console.error('加载处理后的图像失败:', error);
        }
    };

    // 渲染处理按钮
    const renderProcessButton = () => {
        if (!uploadedImage) return null;

        switch (processingStatus) {
            case 'processing':
                return (
                    <button type="button" disabled>
                        正在处理...
                    </button>
                );
            case 'completed':
                return (
                    <button type="button" disabled>
                        处理完成
                    </button>
                );
            case 'failed':
                return (
                    <button 
                        type="button" 
                        className="error-button"
                        onClick={() => startProcessing(uploadedImage.id)}
                    >
                        重试处理
                    </button>
                );
            default:
                return (
                    <button 
                        type="button"
                        onClick={() => startProcessing(uploadedImage.id)}
                    >
                        开始处理
                    </button>
                );
        }
    };

    // 渲染分析结果
    const renderAnalysisResults = () => {
        if (!analysisResults) return null;
        return (
            <div style={{ marginTop: 16 }}>
                <h4>分析结果</h4>
                <Table
                    dataSource={[
                        { key: 'gm', name: '灰质体积', value: `${(analysisResults.gm_volume / 1000).toFixed(2)} ml` },
                        { key: 'wm', name: '白质体积', value: `${(analysisResults.wm_volume / 1000).toFixed(2)} ml` },
                        { key: 'csf', name: '脑脊液体积', value: `${(analysisResults.csf_volume / 1000).toFixed(2)} ml` },
                        { key: 'tiv', name: '颅内总体积', value: `${(analysisResults.tiv / 1000).toFixed(2)} ml` }
                    ]}
                    columns={[
                        { title: '指标', dataIndex: 'name', key: 'name' },
                        { title: '数值', dataIndex: 'value', key: 'value' }
                    ]}
                    pagination={false}
                />
            </div>
        );
    };

    // 受保护的路由组件
    const ProtectedRoute = ({ children }) => {
        const [authState] = useReducer(authReducer, {
            isAuthenticated: !!localStorage.getItem('token'),
            user: null
        });
        
        if (!authState.isAuthenticated) {
            return <Navigate to="/login" />;
        }
        return children;
    };

    // 返回 JSX 元素，用于渲染组件的 UI
    return (
        <div className="app-container">
            <nav className="main-nav">
                <Link to="/">首页</Link>
                <Link to="/patients">患者管理</Link>
                <Link to="/upload">图像上传</Link>
                <Link to="/data">数据分析</Link>
                <Link to="/login" onClick={() => {
                    localStorage.removeItem('token');
                    authDispatch({ type: 'LOGOUT' });
                    navigate('/login');
                }}>退出登录</Link>
            </nav>
            <main className="main-content">
                <div className="home-page">
                    <h1>MRI图像处理系统</h1>
                    <div className="feature-grid">
                        <Link to="/patients" className="feature-card">
                            <h3>患者管理</h3>
                            <p>管理患者信息，查看患者历史记录</p>
                        </Link>
                        <Link to="/upload" className="feature-card">
                            <h3>图像上传</h3>
                            <p>上传MRI图像，进行预处理</p>
                        </Link>
                        <Link to="/data" className="feature-card">
                            <h3>数据分析</h3>
                            <p>查看分析结果，对比处理前后的图像</p>
                        </Link>
                    </div>
                </div>
            </main>
        </div>
    );
};

// 添加加载中组件
const LoadingSpinner = () => (
  <div className="loading-spinner">
    <div className="spinner"></div>
    <p>加载中...</p>
  </div>
);

// 新的 App 组件，包装 Router
function App() {
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    useEffect(() => {
        const token = localStorage.getItem('token');
        setIsAuthenticated(!!token);
    }, []);

    return (
        <Router>
            <Suspense fallback={<LoadingSpinner />}>
                <Routes>
                    <Route path="/login" element={
                        !isAuthenticated ? (
                            <Login onLoginSuccess={() => setIsAuthenticated(true)} />
                        ) : (
                            <Navigate to="/" replace />
                        )
                    } />
                    <Route path="/register" element={
                        !isAuthenticated ? (
                            <Register />
                        ) : (
                            <Navigate to="/" replace />
                        )
                    } />
                    <Route path="/" element={
                        isAuthenticated ? (
                            <MainApp />
                        ) : (
                            <Navigate to="/login" replace />
                        )
                    } />
                    <Route path="/patients" element={
                        <ProtectedRoute>
                            <PatientList />
                        </ProtectedRoute>
                    } />
                    <Route path="/patients/:id" element={
                        <ProtectedRoute>
                            <PatientDetail />
                        </ProtectedRoute>
                    } />
                    <Route path="/upload" element={
                        <ProtectedRoute>
                            <ImageUpload />
                        </ProtectedRoute>
                    } />
                    <Route path="/data" element={
                        <ProtectedRoute>
                            <DataManager />
                        </ProtectedRoute>
                    } />
                    <Route path="/viewer/:imageId" element={
                        <ProtectedRoute>
                            <ImageViewer />
                        </ProtectedRoute>
                    } />
                    <Route path="/compare/:imageId" element={
                        <ProtectedRoute>
                            <ComparisonView />
                        </ProtectedRoute>
                    } />
                </Routes>
            </Suspense>
        </Router>
    );
}

// 导出 App 组件
export default App;
