import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import '../../styles/Auth.css';

const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:5000';

function Login({ onLogin }) {
    const [credentials, setCredentials] = useState({
        username: '',
        password: ''
    });
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setLoading(true);

        try {
            console.log('尝试登录:', credentials.username);
            const response = await axios.post(`${API_BASE}/api/auth/login`, credentials);
            console.log('登录响应:', response.data);

            if (response.data.token) {
                localStorage.setItem('token', response.data.token);
                onLogin(response.data.user);
                navigate('/');
            } else {
                setError('登录失败：未收到token');
            }
        } catch (error) {
            console.error('登录错误:', error.response?.data || error);
            setError(error.response?.data?.error || '登录失败，请重试');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="auth-container">
            <div className="auth-box">
                <h2>登录</h2>
                {error && <div className="error-message">{error}</div>}
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label>用户名</label>
                        <input
                            type="text"
                            value={credentials.username}
                            onChange={(e) => setCredentials({...credentials, username: e.target.value})}
                            required
                        />
                    </div>
                    <div className="form-group">
                        <label>密码</label>
                        <input
                            type="password"
                            value={credentials.password}
                            onChange={(e) => setCredentials({...credentials, password: e.target.value})}
                            required
                        />
                    </div>
                    <button type="submit" disabled={loading}>
                        {loading ? '登录中...' : '登录'}
                    </button>
                </form>
                <p className="auth-link">
                    还没有账号？ <Link to="/register">立即注册</Link>
                </p>
            </div>
        </div>
    );
}

export default Login;
