import React, { useState } from 'react';
import axios from 'axios';
import { message, Upload, Button, Space, Card } from 'antd';
import { UploadOutlined } from '@ant-design/icons';

const ImageUpload = ({ patientId }) => {
  const [fileList, setFileList] = useState([]);
  const [uploading, setUploading] = useState(false);
  const [processing, setProcessing] = useState(false);
  const [previewImage, setPreviewImage] = useState(null);
  const [uploadedImageId, setUploadedImageId] = useState(null);

  const handleUpload = async () => {
    const formData = new FormData();
    fileList.forEach(file => {
      formData.append('file', file);
    });
    formData.append('patient_id', patientId);

    setUploading(true);

    try {
      const token = localStorage.getItem('token');
      const response = await axios.post('http://localhost:5000/api/upload', formData, {
        headers: {
          'Authorization': `Bearer ${token}`,
        }
      });

      if (response.data.status === 'success') {
        setUploadedImageId(response.data.file_info.id);
        message.success('上传成功');
        
        // 获取预览图
        const previewResponse = await axios.get(`http://localhost:5000/api/preview/${response.data.file_info.filename}`, {
          headers: {
            'Authorization': `Bearer ${token}`,
          }
        });
        
        if (previewResponse.data.status === 'success') {
          setPreviewImage(`data:image/png;base64,${previewResponse.data.image}`);
        }
      }
    } catch (error) {
      console.error('上传错误:', error);
      message.error('上传失败');
    } finally {
      setUploading(false);
      setFileList([]);
    }
  };

  const handleProcess = async () => {
    if (!uploadedImageId) {
      message.error('请先上传图像');
      return;
    }

    setProcessing(true);
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(`http://localhost:5000/api/process/${uploadedImageId}`, {}, {
        headers: {
          'Authorization': `Bearer ${token}`,
        }
      });

      if (response.data.status === 'processing') {
        message.success('开始处理图像');
        // 这里可以添加轮询任务状态的逻辑
      }
    } catch (error) {
      console.error('处理错误:', error);
      message.error('处理失败');
    } finally {
      setProcessing(false);
    }
  };

  const uploadProps = {
    onRemove: file => {
      const index = fileList.indexOf(file);
      const newFileList = fileList.slice();
      newFileList.splice(index, 1);
      setFileList(newFileList);
    },
    beforeUpload: file => {
      setFileList([file]);
      return false;
    },
    fileList,
  };

  return (
    <div style={{ maxWidth: 800, margin: '0 auto', padding: '20px' }}>
      <Card title="图像上传与处理">
        <Space direction="vertical" style={{ width: '100%' }}>
          <Upload {...uploadProps}>
            <Button icon={<UploadOutlined />}>选择文件</Button>
          </Upload>
          
          <Button
            type="primary"
            onClick={handleUpload}
            disabled={fileList.length === 0}
            loading={uploading}
            style={{ marginTop: 16 }}
          >
            {uploading ? '上传中' : '开始上传'}
          </Button>

          {previewImage && (
            <div style={{ marginTop: 16 }}>
              <h3>图像预览</h3>
              <img 
                src={previewImage} 
                alt="预览" 
                style={{ maxWidth: '100%', maxHeight: 400 }}
              />
            </div>
          )}

          {uploadedImageId && (
            <Button
              type="primary"
              onClick={handleProcess}
              loading={processing}
              style={{ marginTop: 16 }}
            >
              {processing ? '处理中' : '开始处理'}
            </Button>
          )}
        </Space>
      </Card>
    </div>
  );
};

export default ImageUpload;
