import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { message, Upload, Button, Space, Card, Progress, List, Select } from 'antd';
import { UploadOutlined } from '@ant-design/icons';
import './ImageUpload.css';

const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:5000';

// 图像压缩函数
const compressImage = async (file) => {
  return new Promise((resolve) => {
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = (event) => {
      const img = new Image();
      img.src = event.target.result;
      img.onload = () => {
        const canvas = document.createElement('canvas');
        const ctx = canvas.getContext('2d');
        
        // 计算新的尺寸
        let width = img.width;
        let height = img.height;
        const maxSize = 1200; // 最大尺寸
        
        if (width > height && width > maxSize) {
          height = Math.round((height * maxSize) / width);
          width = maxSize;
        } else if (height > maxSize) {
          width = Math.round((width * maxSize) / height);
          height = maxSize;
        }
        
        canvas.width = width;
        canvas.height = height;
        
        // 绘制压缩后的图像
        ctx.drawImage(img, 0, 0, width, height);
        
        // 转换为Blob
        canvas.toBlob((blob) => {
          resolve(new File([blob], file.name, {
            type: 'image/jpeg',
            lastModified: Date.now()
          }));
        }, 'image/jpeg', 0.7); // 0.7是压缩质量
      };
    };
  });
};

const ImageUpload = () => {
  const [fileList, setFileList] = useState([]);
  const [uploading, setUploading] = useState(false);
  const [processing, setProcessing] = useState(false);
  const [previewImage, setPreviewImage] = useState(null);
  const [uploadedImageId, setUploadedImageId] = useState(null);
  const [progress, setProgress] = useState(0);
  const [processingLogs, setProcessingLogs] = useState([]);
  const [selectedPatient, setSelectedPatient] = useState(null);
  const [patients, setPatients] = useState([]);
  const [imageLoading, setImageLoading] = useState(false);

  // 获取患者列表
  useEffect(() => {
    const fetchPatients = async () => {
      try {
        const response = await axios.get(`${API_BASE}/api/patients`, {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        });
        setPatients(response.data.patients);
      } catch (error) {
        message.error('获取患者列表失败');
      }
    };
    fetchPatients();
  }, []);

  const handleUpload = async () => {
    if (!selectedPatient) {
      message.error('请先选择患者');
      return;
    }

    setUploading(true);
    setProgress(0);

    try {
      const token = localStorage.getItem('token');
      
      // 压缩图像
      const compressedFile = await compressImage(fileList[0]);
      
      const formData = new FormData();
      formData.append('file', compressedFile);
      formData.append('patient_id', selectedPatient);

      const response = await axios.post(`${API_BASE}/api/upload`, formData, {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
        onUploadProgress: (progressEvent) => {
          const percentCompleted = Math.round((progressEvent.loaded * 100) / progressEvent.total);
          setProgress(percentCompleted);
        }
      });

      if (response.data.status === 'success') {
        setUploadedImageId(response.data.file_info.id);
        message.success('上传成功');
        
        // 获取预览图
        setImageLoading(true);
        try {
          const previewResponse = await axios.get(`${API_BASE}/api/preview/${response.data.file_info.filename}`, {
            headers: {
              'Authorization': `Bearer ${token}`,
            }
          });
          
          if (previewResponse.data.status === 'success') {
            setPreviewImage(`data:image/png;base64,${previewResponse.data.image}`);
          }
        } catch (error) {
          console.error('获取预览图失败:', error);
          message.error('获取预览图失败');
        } finally {
          setImageLoading(false);
        }
      }
    } catch (error) {
      console.error('上传错误:', error);
      message.error('上传失败');
    } finally {
      setUploading(false);
      setProgress(0);
      setFileList([]);
    }
  };

  const handleProcess = async () => {
    if (!uploadedImageId) {
      message.error('请先上传图像');
      return;
    }

    setProcessing(true);
    setProcessingLogs([]);

    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(`${API_BASE}/api/process/${uploadedImageId}`, {}, {
        headers: {
          'Authorization': `Bearer ${token}`,
        }
      });

      if (response.data.status === 'processing') {
        message.success('开始处理图像');
        // 开始轮询处理状态
        pollProcessingStatus(response.data.task_id);
      }
    } catch (error) {
      console.error('处理错误:', error);
      message.error('处理失败');
      setProcessing(false);
    }
  };

  const pollProcessingStatus = async (taskId) => {
    const interval = setInterval(async () => {
      try {
        const response = await axios.get(`${API_BASE}/api/tasks/${taskId}`, {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`,
          }
        });

        const { status, progress, logs } = response.data;
        setProgress(progress);
        setProcessingLogs(logs);

        if (status === 'completed') {
          clearInterval(interval);
          setProcessing(false);
          message.success('处理完成');
        } else if (status === 'failed') {
          clearInterval(interval);
          setProcessing(false);
          message.error('处理失败');
        }
      } catch (error) {
        console.error('获取处理状态失败:', error);
        clearInterval(interval);
        setProcessing(false);
        message.error('获取处理状态失败');
      }
    }, 2000);

    return () => clearInterval(interval);
  };

  const uploadProps = {
    onRemove: file => {
      const index = fileList.indexOf(file);
      const newFileList = fileList.slice();
      newFileList.splice(index, 1);
      setFileList(newFileList);
    },
    beforeUpload: file => {
      setFileList([file]);
      return false;
    },
    fileList,
  };

  return (
    <div className="image-upload-container">
      <Card title="图像上传与处理">
        <Space direction="vertical" style={{ width: '100%' }}>
          <div className="patient-selector">
            <Select
              style={{ width: '100%' }}
              placeholder="请选择患者"
              onChange={setSelectedPatient}
              options={patients.map(p => ({ label: p.name, value: p.id }))}
            />
          </div>

          <Upload {...uploadProps}>
            <Button icon={<UploadOutlined />}>选择文件</Button>
          </Upload>
          
          <Button
            type="primary"
            onClick={handleUpload}
            disabled={fileList.length === 0 || !selectedPatient}
            loading={uploading}
            style={{ marginTop: 16 }}
          >
            {uploading ? '上传中' : '开始上传'}
          </Button>

          {progress > 0 && (
            <Progress percent={progress} status={progress === 100 ? "success" : "active"} />
          )}

          {previewImage && (
            <div className="preview-section">
              <h3>图像预览</h3>
              {imageLoading ? (
                <div className="image-loading">
                  <div className="spinner"></div>
                  <p>加载预览图...</p>
                </div>
              ) : (
                <img 
                  src={previewImage} 
                  alt="预览" 
                  className="preview-image"
                  loading="lazy"
                />
              )}
            </div>
          )}

          {uploadedImageId && (
            <Button
              type="primary"
              onClick={handleProcess}
              loading={processing}
              style={{ marginTop: 16 }}
            >
              {processing ? '处理中' : '开始处理'}
            </Button>
          )}

          {processingLogs.length > 0 && (
            <div className="processing-logs">
              <h3>处理日志</h3>
              <List
                size="small"
                bordered
                dataSource={processingLogs}
                renderItem={item => <List.Item>{item}</List.Item>}
              />
            </div>
          )}
        </Space>
      </Card>
    </div>
  );
};

export default ImageUpload;
