  taskId: null,
  status: 'idle',
  // 'idle' | 'uploading' | 'processing' | 'completed' | 'failed'
  progress: 0,
  result: null,
  error: null
};
function uploadReducer(state, action) {
  switch (action.type) {
    case 'START_UPLOAD':
      return {
        ...state,
        status: 'uploading',
        progress: 0
      };
    case 'UPDATE_PROGRESS':
      return {
        ...state,
        progress: action.payload
      };
    case 'PROCESS_START':
      return {
        ...state,
        status: 'processing',
        taskId: action.payload
      };
    case 'SUCCESS':
      return {
        ...state,
        status: 'completed',
        result: action.payload
      };
    case 'ERROR':
      return {
        ...state,
        status: 'failed',
        error: action.payload
      };
    case 'RESET':
      return initialState;
    default:
      return state;
  }
}

